import { Component, OnInit } from '@angular/core';
import {FormControl} from '@angular/forms';
import { MatDialog } from "@angular/material/dialog";
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-pop-up',
  templateUrl: './pop-up.component.html',
  styleUrls: ['./pop-up.component.css']
})
export class PopUpComponent implements OnInit {
  date = new FormControl(new Date());
  serializedDate = new FormControl(new Date().toISOString());

  tableItem:any;
  StatusID = '1';
  Username = 'John Doe';
  Description = 'Test';
  FLP = false;
  FEMA = false;
  Date = String(this.date);
  TableItemUrl = 'https://fpvsdbdjs6.execute-api.us-east-2.amazonaws.com/dev/insertItem/'
  +this.StatusID+"?Username="+this.Username+"&Description="+this.Description+"&FLP="
  +this.FLP+"?FEMA="+this.FEMA+"?";

  onNoClick(): void {
    this.http.get(this.TableItemUrl+this.StatusID).subscribe({
      next: data => {
        console.log(data);
      },
      error: err => {
        console.log(err);
        this.tableItem = 'Error invoking API';
      }
    });
    this.dialogRef.closeAll();
  }
  constructor(private dialogRef: MatDialog, private http:HttpClient){

  }

  ngOnInit() {

  }

}
<div class="content"   role="main"style="width: 300px; padding: 2px 20px;
margin: 10px 0px;
height: 470px;" >
<h2>Add Report</h2>



  <form class="example-form" >
    <mat-form-field class="example-full-width" >
      <mat-label>Employee Name</mat-label>
      <input matInput placeholder="John Doe" value="John Doe" [disabled]="true">
    </mat-form-field>
  </form>

  <mat-form-field appearance="fill">
    <mat-label>Date</mat-label>
    <input matInput [matDatepicker]="picker3" [value]="date.value">
    <mat-hint>MM/DD/YYYY</mat-hint>
    <mat-datepicker-toggle matIconSuffix [for]="picker3"></mat-datepicker-toggle>
    <mat-datepicker #picker3></mat-datepicker>
  </mat-form-field>

  <form class="example-form">

    <mat-form-field style="width: 300px;
    height: 150px;">
      <mat-label>Report Text Goes Here</mat-label>
      <textarea matInput cdkTextareaAutosize
      #autosize="cdkTextareaAutosize"
      cdkAutosizeMinRows="1"
      cdkAutosizeMaxRows="6"
       placeholder=""></textarea>
    </mat-form-field>

  </form>
  <h2>Select Project(s)</h2>
  <section>
    <mat-checkbox class="example-margin">FLP</mat-checkbox>
    <mat-checkbox class="example-margin">FEMA</mat-checkbox>
  </section>
  <button mat-raised-button (click)="onNoClick()"><mat-icon>cancel</mat-icon>Cancel</button>
  <button mat-stroked-button (click)="onNoClick()" style="background-color:orange; color: white;"><mat-icon>save</mat-icon>Save</button>

</div>
