  <table mat-table [dataSource]="dataSource" matSort class="mat-elevation-z8">

    <!--- Note that these columns can be defined in any order.
          The actual rendered columns are set as a property on the row definition" -->

    <!-- Position Column -->
    <ng-container matColumnDef="date">
      <th mat-header-cell *matHeaderCellDef mat-sort-header  class="orange"> Date </th>
      <td mat-cell *matCellDef="let element"> {{element.date}} </td>
    </ng-container>

    <!-- Name Column -->
    <ng-container matColumnDef="projects">
      <th class="orange" mat-header-cell mat-sort-header *matHeaderCellDef> Projects </th>
      <td mat-cell *matCellDef="let element"> {{element.projects}} </td>
    </ng-container>

    <!-- Weight Column -->
    <ng-container  matColumnDef="reportStatus">
      <th class="orange" mat-header-cell mat-sort-header *matHeaderCellDef> Report Status </th>
      <td mat-cell *matCellDef="let element"> {{element.reportStatus}} </td>
    </ng-container>



    <tr class="orange"  mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr  mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
    <tr></tr>


  </table>

  <div class="example-form-fields">

    <mat-form-field>
      <mat-label>Filter	<mat-icon>filter_list</mat-icon></mat-label>
      <input matInput placeholder="Filter" (keyup)="Filterchange($event)">

    </mat-form-field>

    <button mat-raised-button  style="height:57px; width: 109px; background-color:orange; color: white;" (click)="openDialog()">
